    private void loginBTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginBTNActionPerformed
        // TODO add your handling code here:
        if((userTF.getText().equals("admin")) &&
                //(new String(passwordTF.getPssword()).equals("admin))){
                (String.valueOf(passwordTF.getPassword()).equals("admin"))){
            new Home().setVisible(true);
            dispose();
        }else{
           JOptionPane.showMessageDialog(
           null, userTF.getText() + ", Password anda salah","Pesan Kesalahan",JOptionPane.ERROR_MESSAGE);
           userTF.setText("");
           passwordTF.setText("");
           userTF.requestFocus();
        }
    }//GEN-LAST:event_loginBTNActionPerformed

